import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const LocationShare = () => {
  const { orderId } = useParams();

  useEffect(() => {
    const getLocation = () => {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          await axios.post(`http://localhost:3001/update-delivery-location/${orderId}`, {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          alert('Location shared successfully!');
        },
        (error) => {
          alert('Please enable location services to share your position.');
        }
      );
    };

    getLocation();
  }, [orderId]);

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Sharing location for order #{orderId}</h2>
      <p>Please allow location access in your browser...</p>
    </div>
  );
};

export default LocationShare;
