// src/pages/OrderTracking.js
import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const OrderTracking = () => {
  const { orderId } = useParams();
  const [order] = useState({
    id: orderId,
    status: 'in_transit',
    pickup: 'Shop A',
    delivery: 'Customer X',
    driverLocation: [51.505, -0.09], // Default location
  });

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.headerTitle}>Order Tracking</h1>
        <Link to="/customer-dashboard" style={styles.navLink}>Back to Dashboard</Link>
      </header>

      <div style={styles.content}>
        <div style={styles.orderDetails}>
          <h2>Order #{order.id}</h2>
          <p><strong>Status:</strong> <span style={styles.status}>{order.status}</span></p>
          <p><strong>Pickup:</strong> {order.pickup}</p>
          <p><strong>Delivery:</strong> {order.delivery}</p>
        </div>

        <div style={styles.mapContainer}>
          <MapContainer
            center={order.driverLocation}
            zoom={13}
            style={styles.map}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; OpenStreetMap contributors'
            />
            <Marker position={order.driverLocation}>
              <Popup>Driver is here!</Popup>
            </Marker>
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

// Keep the same styles as before
const styles = { /* ... */ };

export default OrderTracking;
