import React, { useState, useCallback } from "react";
import axios from "axios";
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
} from "@react-google-maps/api";

const containerStyle = {
  width: "100%",
  height: "300px",
};

const center = {
  lat: 37.7749, // default center (San Francisco)
  lng: -122.4194,
};

const CreateOrder = () => {
  const [serialNumber, setSerialNumber] = useState("");
  const [originAddress, setOriginAddress] = useState("");
  const [hasPhoneNumber, setHasPhoneNumber] = useState(null);
  const [recipientPhone, setRecipientPhone] = useState("");
  const [manualDeliveryAddress, setManualDeliveryAddress] = useState("");
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState("cash");
  const [packageType, setPackageType] = useState("simple");
  const [totalAmount, setTotalAmount] = useState(0);
  const [orderSuccess, setOrderSuccess] = useState(false);

  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY", // Replace with your API key
  });

  const generateWhatsAppLink = () => {
    const msg = encodeURIComponent(
      `New delivery from ${originAddress}. Please be ready.`
    );
    const number = recipientPhone.replace(/\D/g, "");
    return `https://wa.me/${number}?text=${msg}`;
  };

  const reverseGeocode = async (lat, lng) => {
    try {
      const response = await axios.get(
        `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=YOUR_GOOGLE_MAPS_API_KEY`
      );
      if (
        response.data.results &&
        response.data.results.length > 0
      ) {
        setManualDeliveryAddress(response.data.results[0].formatted_address);
      } else {
        alert("Unable to find address for selected location.");
      }
    } catch (error) {
      console.error("Geocoding error:", error);
    }
  };

  const handleMapClick = useCallback((e) => {
    const lat = e.latLng.lat();
    const lng = e.latLng.lng();
    setSelectedLocation({ lat, lng });
    reverseGeocode(lat, lng);
  }, []);

  const handleCreateOrder = async () => {
    const customerId = localStorage.getItem("shopOwnerId");
    if (!customerId) {
      alert("You must be logged in to place an order.");
      return;
    }

    let deliveryAddress = "";
    if (hasPhoneNumber && recipientPhone) {
      deliveryAddress = generateWhatsAppLink();
    } else {
      deliveryAddress = manualDeliveryAddress;
    }

    try {
      const response = await axios.post("http://localhost:3001/orders", {
        customerId,
        packageType,
        serialNumber,
        originAddress,
        deliveryAddress,
        paymentMethod,
        totalAmount,
      });

      if (response.status === 201) {
        setOrderSuccess(true);
        setSerialNumber("");
        setOriginAddress("");
        setRecipientPhone("");
        setManualDeliveryAddress("");
        setHasPhoneNumber(null);
        setSelectedLocation(null);
        setPackageType("simple");
        setPaymentMethod("cash");
        setTotalAmount(0);
      }
    } catch (error) {
      console.error("Order creation failed:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Create New Order</h2>

      <div>
        <label>Serial Number:</label>
        <input
          type="text"
          value={serialNumber}
          onChange={(e) => setSerialNumber(e.target.value)}
        />
      </div>

      <div>
        <label>Origin Address:</label>
        <input
          type="text"
          value={originAddress}
          onChange={(e) => setOriginAddress(e.target.value)}
        />
      </div>

      <div>
        <label>Do you have the recipient’s phone number?</label>
        <div>
          <button onClick={() => setHasPhoneNumber(true)}>Yes</button>
          <button onClick={() => setHasPhoneNumber(false)}>No</button>
        </div>
      </div>

      {hasPhoneNumber === true && (
        <div>
          <label>Recipient Phone Number (with country code):</label>
          <input
            type="text"
            placeholder="+1234567890"
            value={recipientPhone}
            onChange={(e) => setRecipientPhone(e.target.value)}
          />
        </div>
      )}

      {hasPhoneNumber === false && (
        <div>
          <label>Click on Map to Select Delivery Address:</label>
          {isLoaded ? (
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={center}
              zoom={10}
              onClick={handleMapClick}
            >
              {selectedLocation && (
                <Marker position={selectedLocation} />
              )}
            </GoogleMap>
          ) : (
            <p>Loading map...</p>
          )}

          <div style={{ marginTop: "10px" }}>
            <label>Selected Address:</label>
            <input
              type="text"
              value={manualDeliveryAddress}
              onChange={(e) =>
                setManualDeliveryAddress(e.target.value)
              }
              placeholder="Street, City, etc."
            />
          </div>
        </div>
      )}

      <div>
        <label>Package Type:</label>
        <select
          value={packageType}
          onChange={(e) => setPackageType(e.target.value)}
        >
          <option value="simple">Simple</option>
          <option value="big">Big</option>
        </select>
      </div>

      <div>
        <label>Payment Method:</label>
        <select
          value={paymentMethod}
          onChange={(e) => setPaymentMethod(e.target.value)}
        >
          <option value="cash">Cash</option>
          <option value="card">Card</option>
        </select>
      </div>

      <div>
        <label>Total Amount:</label>
        <input
          type="number"
          value={totalAmount}
          onChange={(e) =>
            setTotalAmount(parseFloat(e.target.value))
          }
        />
      </div>

      <button onClick={handleCreateOrder}>Create Order</button>

      {orderSuccess && (
        <p style={{ color: "green" }}>Order created successfully!</p>
      )}
    </div>
  );
};

export default CreateOrder;
