import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const CreateOrder = () => {
  const navigate = useNavigate();
  const [orderType, setOrderType] = useState('simple');
  const [serialNumber, setSerialNumber] = useState('');
  const [packagePrice, setPackagePrice] = useState('');
  const [originAddress, setOriginAddress] = useState('');
  const [originCoords, setOriginCoords] = useState(null);
  const [editingOrigin, setEditingOrigin] = useState(false);
  const [deliveryPhone, setDeliveryPhone] = useState('');
  const [showMap, setShowMap] = useState(false);
  const [deliveryCoords, setDeliveryCoords] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [predictedPrice, setPredictedPrice] = useState(0);
  const [length, setLength] = useState('');
  const [weight, setWeight] = useState('');
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [customerId, setCustomerId] = useState(null);
  const [distance, setDistance] = useState(0);

  const mapContainerStyle = {
    height: '300px',
    width: '100%',
    borderRadius: '12px',
    overflow: 'hidden',
    boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
    transition: 'all 0.3s ease'
  };

  useEffect(() => {
    const fetchCustomerData = async () => {
      const storedId = localStorage.getItem('shopOwnerId');
      if (storedId) {
        setCustomerId(storedId);
        try {
          const response = await axios.get(`http://localhost:3001/customers/${storedId}`);
          setOriginAddress(response.data.shop_address);
          const [lat, lng] = response.data.shop_address.split(',').map(Number);
          setOriginCoords({ lat, lng });
        } catch (err) {
          console.error('Error fetching customer data:', err);
        }
      }
    };
    fetchCustomerData();
  }, []);

  const handleMapClickOrigin = (e) => {
    setOriginCoords({
      lat: e.latLng.lat(),
      lng: e.latLng.lng()
    });
  };

  const saveNewOrigin = async () => {
    try {
      await axios.put(`http://localhost:3001/customers/update-address/${customerId}`, originCoords);
      setOriginAddress(`${originCoords.lat},${originCoords.lng}`);
      setEditingOrigin(false);
    } catch (err) {
      console.error('Failed to update origin address:', err);
    }
  };

  const handleMapClick = (e) => {
    setDeliveryCoords({
      lat: e.latLng.lat(),
      lng: e.latLng.lng()
    });
  };

  const calculateDistance = async () => {
    if (!deliveryCoords || !originAddress) return;

    try {
      const response = await axios.post('http://localhost:3001/calculate-distance', {
        origin: originAddress,
        destination:`${deliveryCoords.lat},${deliveryCoords.lng}`
      });
      setDistance(response.data.distance);
    } catch (err) {
      console.error('Distance calculation error:', err);
    }
  };

  useEffect(() => {
    if (orderType === 'package' && deliveryCoords) {
      calculateDistance();
    }
  }, [deliveryCoords, orderType]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (orderType === 'package' && !deliveryCoords) {
      setError('Please select a delivery location on the map');
      return;
    }

    setIsSubmitting(true);

    const orderData = {
      customerId,
      orderType,
      serialNumber: serialNumber || null,
      originAddress,
      deliveryInfo: showMap ? deliveryCoords : deliveryPhone,
      paymentMethod,
      packagePrice: parseFloat(packagePrice),
      deliveryFee: predictedPrice,
      totalPrice: (parseFloat(packagePrice) || 0) + predictedPrice,
      ...(orderType === 'package' && {
        length: parseFloat(length),
        weight: parseFloat(weight),
        distance: distance
      })
    };

    try {
      const response = await axios.post('http://localhost:3001/orders', orderData);
      if (response.data.success) {
        navigate('/shop-owner-dashboard');
      }
    } catch (err) {
      setError('Failed to create order');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '900px', margin: '0 auto' }}>
      <h2>Create New Order</h2>
      <form onSubmit={handleSubmit}>
        <label>Package Price ($)</label>
        <input type="number" value={packagePrice} onChange={(e) => setPackagePrice(e.target.value)} required />

        <label>Order Type</label>
        <select value={orderType} onChange={(e) => setOrderType(e.target.value)}>
          <option value="simple">Simple</option>
          <option value="package">Package</option>
        </select>

        {orderType === 'package' && (
          <>
            <label>Length (cm)</label>
            <input type="number" value={length} onChange={(e) => setLength(e.target.value)} required />
            <label>Weight (kg)</label>
            <input type="number" value={weight} onChange={(e) => setWeight(e.target.value)} required />
          </>
        )}

        <label>Origin Address</label>
        <p>{originAddress}</p>

        <div style={{ marginBottom: '10px' }}>
          <button type="button" onClick={() => setEditingOrigin(!editingOrigin)}>
            {editingOrigin ? 'Cancel' : 'Change Origin Address'}
          </button>
        </div>

        {editingOrigin && (
          <div style={{ marginBottom: '15px' }}>
            <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
              <GoogleMap
                mapContainerStyle={mapContainerStyle}
                center={originCoords || { lat: 33.89, lng: 35.5 }}
                zoom={13}
                onClick={handleMapClickOrigin}
              >
                {originCoords && <Marker position={originCoords} />}
              </GoogleMap>
            </LoadScript>
            <button type="button" onClick={saveNewOrigin} style={{ marginTop: '10px' }}>
              Save New Origin
            </button>
          </div>
        )}

        <label>Delivery Method</label>
        <div>
          <button type="button" onClick={() => setShowMap(false)}>WhatsApp</button>
          <button type="button" onClick={() => setShowMap(true)}>Select on Map</button>
        </div>

        {showMap ? (
          <div>
            <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
              <GoogleMap
                mapContainerStyle={mapContainerStyle}
                center={{ lat: 33.89, lng: 35.5 }}
                zoom={12}
                onClick={handleMapClick}
              >
                {deliveryCoords && <Marker position={deliveryCoords} />}
              </GoogleMap>
            </LoadScript>
            {deliveryCoords && <p>Selected: {deliveryCoords.lat.toFixed(4)}, {deliveryCoords.lng.toFixed(4)}</p>}
          </div>
        ) : (
          <input
            type="text"
            placeholder="Recipient WhatsApp"
            value={deliveryPhone}
            onChange={(e) => setDeliveryPhone(e.target.value)}
            required
          />
        )}

        <label>Payment Method</label>
        <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
          <option value="cash">Cash</option>
          <option value="wallet">Wallet</option>
        </select>

        <p>Total: ${(parseFloat(packagePrice || 0) + predictedPrice).toFixed(2)}</p>

        {error && <p style={{ color: 'red' }}>{error}</p>}

        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Creating...' : 'Create Order'}
        </button>
      </form>
    </div>
  );
};




