import React, { useEffect, useState } from 'react';

const Wallet = () => {
  const [wallet, setWallet] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const userType = localStorage.getItem('userType');
  let userId;

  if (userType === 'customer') {
    userId = localStorage.getItem('shopOwnerId');
  } else if (userType === 'driver') {
    userId = localStorage.getItem('driverId');
  } else if (userType === 'admin') {
    userId = localStorage.getItem('adminId'); // fix here
  }

  useEffect(() => {
    if (!userType || !userId) {
      setError('User not logged in or invalid.');
      setLoading(false);
      return;
    }

    const fetchWallet = async () => {
      try {
        const response = await fetch(`http://localhost:3001/wallet/${userType}/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch wallet data');
        }

        const data = await response.json();
        setWallet(data);
        setError('');
      } catch (err) {
        setError(err.message);
        setWallet(null);
      } finally {
        setLoading(false);
      }
    };

    fetchWallet();
  }, [userType, userId]);

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Wallet</h2>
      {loading && <p>Loading...</p>}
      {error && <p style={styles.error}>{error}</p>}
      {wallet && (
        <div style={styles.card}>
          <p><strong>Balance:</strong> â‚¹{wallet.balance}</p>
          <p><strong>Last Updated:</strong> {new Date(wallet.updated_at).toLocaleString()}</p>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '500px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
    textAlign: 'center',
  },
  card: {
    padding: '15px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
  },
  error: {
    color: 'red',
  },
};

export default Wallet;
