import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const DriverDashboard = () => {
  const navigate = useNavigate();
  const [availableOrders, setAvailableOrders] = useState([]);
  const [currentOrders, setCurrentOrders] = useState([]);
  const [balance, setBalance] = useState('$0.00');
  const [isVerified, setIsVerified] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const driverId = localStorage.getItem("driverId"); // Fetch driver ID from localStorage
      if (!driverId) {
        setError("Driver not logged in. Please log in.");
        setLoading(false);
        return;
      }

      try {
        // Fetch available orders
        const availableOrdersResponse = await axios.get(`http://localhost:3000/orders/available`);
        setAvailableOrders(availableOrdersResponse.data);

        // Fetch current orders for the driver
        const currentOrdersResponse = await axios.get(`http://localhost:3000/orders/current/${driverId}`);
        setCurrentOrders(currentOrdersResponse.data);

        // Fetch wallet balance
        const walletResponse = await axios.get(`http://localhost:3000/wallet/${driverId}`);
        setBalance(`$${walletResponse.data.balance.toFixed(2)}`);

        // Fetch verification status
        const verificationResponse = await axios.get(`http://localhost:3000/verification/${driverId}`);
        setIsVerified(verificationResponse.data.isVerified);
      } catch (err) {
        setError('Failed to fetch data');
        console.error('Fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAcceptOrder = async (orderId) => {
    const driverId = localStorage.getItem("driverId"); // Get driver ID from localStorage
    if (!driverId) {
      setError("Driver not logged in.");
      setLoading(false);
      navigate('/login');
      return;
    }

    try {
      // Accept order (update status in the database)
      await axios.post('http://localhost:3000/orders/accept', { orderId, driverId });
      navigate('/current-orders');
    } catch (err) {
      setError('Failed to accept order');
      console.error('Accept order error:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("driverId"); // Remove driver ID from localStorage on logout
    navigate('/login'); // Redirect to login page
  };

  if (loading) return <p style={styles.loadingText}>Loading...</p>;
  if (error) return <p style={styles.errorText}>{error}</p>;

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.headerTitle}>Driver Dashboard</h1>
        <nav style={styles.nav}>
          <Link to="/profile" style={styles.navLink}>Profile</Link>
          <Link to="/wallet" style={styles.navLink}>Wallet</Link>
          <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
        </nav>
      </header>

      <div style={styles.content}>
        <div style={styles.statusSection}>
          <div style={styles.balanceCard}>
            <h3>Wallet Balance</h3>
            <p style={styles.balanceAmount}>{balance}</p>
            <Link to="/wallet" style={styles.walletLink}>View Transactions</Link>
          </div>
          <div style={styles.verificationCard}>
            <h3>ID Verification</h3>
            {isVerified ? (
              <p style={styles.verifiedText}>✅ Verified</p>
            ) : (
                <>
                <p style={styles.notVerifiedText}>❌ Not Verified</p>
                <Link to="/id-verification" style={styles.navLink}>ID Verification</Link>
                </>
              )}
          </div>
        </div>

        <div style={styles.ordersSection}>
          <h2>Available Orders</h2>
          <div style={styles.ordersList}>
            {availableOrders.map((order) => (
              <div key={order.id} style={styles.orderCard}>
                <div>
                  <p><strong>Order #{order.id}</strong></p>
                  <p>Pickup: {order.pickup_address}</p>
                  <p>Delivery: {order.delivery_address}</p>
                  <p>Amount: ${order.total_amount.toFixed(2)}</p>
                </div>
                <button
                  onClick={() => handleAcceptOrder(order.id)}
                  style={styles.acceptButton}
                >
                  Accept Order
                </button>
              </div>
            ))}
          </div>
        </div>

        <div style={styles.ordersSection}>
          <h2>Current Orders</h2>
          <div style={styles.ordersList}>
            {currentOrders.map((order) => (
              <div key={order.id} style={styles.orderCard}>
                <div>
                  <p><strong>Order #{order.id}</strong></p>
                  <p>Pickup: {order.pickup_address}</p>
                  <p>Delivery: {order.delivery_address}</p>
                  <p>Status: <span style={styles.status}>{order.status}</span></p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  // Styles are the same as your previous code
};

export default DriverDashboard;
