import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreateOrder = () => {
  const navigate = useNavigate();
  const [customerId, setCustomerId] = useState(null);
  const [packageType, setPackageType] = useState('simple'); // simple or big
  const [serialNumber, setSerialNumber] = useState('');
  const [originAddress, setOriginAddress] = useState('');
  const [deliveryType, setDeliveryType] = useState('whatsapp'); // whatsapp or map
  const [deliveryAddress, setDeliveryAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [basePrice, setBasePrice] = useState(0);
  const [deliveryFee, setDeliveryFee] = useState(0);
  const [totalAmount, setTotalAmount] = useState(0);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Get customer ID on mount
  useEffect(() => {
    const id = localStorage.getItem('shopOwnerId');
    if (id) {
      setCustomerId(id);
    } else {
      setError('Customer not logged in.');
    }
  }, []);

  // Get origin address from backend
  useEffect(() => {
    const fetchOrigin = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/customers/${localStorage.getItem('shopOwnerId')}`);
        setOriginAddress(res.data.shop_address || '');
      } catch (err) {
        console.error('Failed to fetch origin:', err);
      }
    };
    fetchOrigin();
  }, []);

  // Set base price when package changes
  useEffect(() => {
    setBasePrice(packageType === 'simple' ? 2 : 5);
  }, [packageType]);

  // Estimate delivery fee when address changes
  useEffect(() => {
    const estimateFee = async () => {
      if (!originAddress || !deliveryAddress) return;
      try {
        const res = await axios.post('http://localhost:3001/predict-fee', {
          origin: originAddress,
          destination: deliveryAddress,
          type: packageType,
        });
        const fee = res.data.fee || 0;
        setDeliveryFee(fee);
        setTotalAmount((packageType === 'simple' ? 2 : 5) + fee);
      } catch (err) {
        console.error('Prediction failed:', err);
      }
    };
    estimateFee();
  }, [originAddress, deliveryAddress, packageType]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!originAddress || !deliveryAddress) {
      setError('Please enter both origin and delivery address.');
      return;
    }

    const orderData = {
      customerId,
      packageType,
      serialNumber,
      originAddress,
      deliveryAddress,
      deliveryType,
      paymentMethod,
      basePrice,
      deliveryFee,
      totalAmount,
    };

    try {
      setLoading(true);
      const res = await axios.post('http://localhost:3001/orders', orderData, {
        withCredentials: true,
        headers: { 'Content-Type': 'application/json' },
      });
      if (res.data.success) {
        navigate('/shop-owner-dashboard');
      } else {
        setError(res.data.message || 'Order creation failed.');
      }
    } catch (err) {
      console.error(err);
      setError('Error submitting order.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2>Create Delivery Order</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        {/* Package Type */}
        <div style={styles.section}>
          <label>Package Type:</label>
          <div style={styles.radioGroup}>
            <label><input type="radio" value="simple" checked={packageType === 'simple'} onChange={() => setPackageType('simple')} /> Simple (Moto)</label>
            <label><input type="radio" value="big" checked={packageType === 'big'} onChange={() => setPackageType('big')} /> Big (Car)</label>
          </div>
        </div>

        {/* Serial Number */}
        <input
          style={styles.input}
          placeholder="Serial Number (optional)"
          value={serialNumber}
          onChange={(e) => setSerialNumber(e.target.value)}
        />

        {/* Origin Address */}
        <textarea
          style={styles.textarea}
          placeholder="Origin Address"
          value={originAddress}
          onChange={(e) => setOriginAddress(e.target.value)}
          required
        />

        {/* Delivery Type */}
        <div style={styles.section}>
          <label>Delivery Method:</label>
          <div style={styles.radioGroup}>
            <label><input type="radio" value="whatsapp" checked={deliveryType === 'whatsapp'} onChange={() => setDeliveryType('whatsapp')} /> WhatsApp</label>
            <label><input type="radio" value="map" checked={deliveryType === 'map'} onChange={() => setDeliveryType('map')} /> Map Address</label>
          </div>
        </div>

        {/* Delivery Address */}
        <textarea
          style={styles.textarea}
          placeholder={deliveryType === 'whatsapp' ? 'WhatsApp Link / Number' : 'Map Address / Coordinates'}
          value={deliveryAddress}
          onChange={(e) => setDeliveryAddress(e.target.value)}
          required
        />

        {/* Payment Method */}
        <select style={styles.select} value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
          <option value="cash">Cash</option>
          <option value="wallet">Wallet</option>
        </select>

        {/* Price Display */}
        <div style={styles.priceBox}>
          <p>Base Price: <strong>${basePrice.toFixed(2)}</strong></p>
          <p>Estimated Delivery Fee: <strong>${deliveryFee.toFixed(2)}</strong></p>
          <p>Total: <strong>${totalAmount.toFixed(2)}</strong></p>
        </div>

        {error && <p style={styles.error}>{error}</p>}

        <button type="submit" disabled={loading} style={styles.button}>
          {loading ? 'Creating...' : 'Create Order'}
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    maxWidth: '700px',
    margin: '0 auto',
    backgroundColor: '#f8f8f8',
    fontFamily: 'Arial, sans-serif',
  },
  form: {
    backgroundColor: '#fff',
    padding: '25px',
    borderRadius: '10px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  input: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  textarea: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    resize: 'vertical',
    minHeight: '70px',
  },
  select: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  section: {
    display: 'flex',
    flexDirection: 'column',
  },
  radioGroup: {
    display: 'flex',
    gap: '20px',
    marginTop: '5px',
  },
  priceBox: {
    backgroundColor: '#f1f1f1',
    padding: '10px',
    borderRadius: '5px',
  },
  error: {
    color: 'red',
  },
  button: {
    padding: '12px',
    backgroundColor: '#28a745',
    color: '#fff',
    fontWeight: 'bold',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default CreateOrder;
